import React, { useState, useCallback, useEffect } from 'react';
import { Button, Form } from 'react-bootstrap';
import axios from "axios";
import Alert from '../Alert/Alert';
import Spinner from '../Spinner/spinner';

interface ChargeData {
    credit_card: string,
    debit_card: string,
    ach: string
}
const initialState: ChargeData = {
    credit_card: '',
    debit_card: '',
    ach: ''
};
const Admin = () => {
    const [alert,setAlert] = useState({});
    const [isLoader, setIsLoader] = React.useState(false);
    const [state, setState] = useState<ChargeData>(initialState);

    useEffect(() => {
        getcharge();
    }, []);

    async function getcharge() {
        const response = await axios.get("/api/charges");
        console.log(JSON.stringify(response.data))
        setState({credit_card: response.data.credit_card,
            debit_card: response.data.debit_card,
            ach: response.data.ach});
    }
    async function updateCharges(data:string) {
        setIsLoader(true);
        var response = await axios.post("/api/update-charges", JSON.parse(data));
        if(response.data.status===true){
            setIsLoader(false)
            setAlert({status:'success',message:'Charges Updated Sucessfully'});
            getcharge();
        }
        else{
            setIsLoader(false)
            setAlert({status:'fail',message:'Something went wrong'});
        }
    }
    const handleFormChange = (event: {
        target: { name: string; value: string };
    }) => {
        const { name, value } = event.target;
        updateStateValues(name, value);
    };


    const updateStateValues = useCallback(
        (keyName: any, value: any) => {
            setState({
                ...state,
                [keyName]: value || '',
            });
        },
        [state],
    );

    function handleSubmitAction() {
        try {
            console.log(JSON.stringify(state))
            updateCharges(JSON.stringify(state));
        } catch (error: any) {
            console.log(error);
        }
    }

    return (
        <>
            <div className="container">
    {isLoader ? <Spinner /> : null}
            <Alert alert={alert} setAlert={setAlert}/>
                <div className="box-right card-bg-img">
                    <p className="fw-bold h7">Charges</p>
                    <Form.Label>Credit Card</Form.Label>
                    <Form.Control
                        type="text"
                        name="credit_card"
                        className="form-control mb-3"
                        autoComplete="off"
                        onChange={handleFormChange}
                        placeholder="Credit card charge"
                        value={state.credit_card}
                    />
                    <Form.Label>Debit Card</Form.Label>
                    <Form.Control
                        type="text"
                        name="debit_card"
                        className="form-control mb-3"
                        autoComplete="off"
                        onChange={handleFormChange}
                        placeholder="Debit card charge"
                        value={state.debit_card}
                    />
                    <Form.Label>ACH</Form.Label>
                    <Form.Control
                        type="text"
                        name="ach"
                        className="form-control mb-3"
                        autoComplete="off"
                        onChange={handleFormChange}
                        placeholder="ACH card charge"
                       value={state.ach}
                    />
                    <Button variant="primary" size="sm" onClick={handleSubmitAction}>
                        Save
                    </Button>
                </div>
            </div>
        </>
    )
}
export default Admin;
